import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [image, setImage] = useState(null);
    const [faceDetectionResult, setFaceDetectionResult] = useState('');
    const [message, setMessage] = useState('');
    const [chatResponse, setChatResponse] = useState('');

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        const reader = new FileReader();
        reader.onloadend = () => {
            setImage(reader.result);
        };
        if (file) {
            reader.readAsDataURL(file);
        }
    };

    const handleDetectFace = async () => {
        if (image) {
            try {
                const response = await axios.post('http://127.0.0.1:5000/detect_face', { image });
                setFaceDetectionResult(response.data.result);
            } catch (error) {
                console.error('Error detecting face:', error);
            }
        }
    };

    const handleChat = async () => {
        if (message) {
            try {
                const response = await axios.post('http://127.0.0.1:5000/chat', { message });
                setChatResponse(response.data.response);
            } catch (error) {
                console.error('Error chatting with LLM:', error);
            }
        }
    };

    return (
        <div className="App">
            <h1>人脸识别与大语言模型交互</h1>
            <input type="file" onChange={handleImageChange} />
            <button onClick={handleDetectFace}>检测人脸</button>
            {faceDetectionResult && <p>{faceDetectionResult}</p>}
            <input
                type="text"
                placeholder="输入消息与大语言模型交互"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={handleChat}>发送消息</button>
            {chatResponse && <p>大语言模型回复: {chatResponse}</p>}
        </div>
    );
}

export default App;
