import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [image, setImage] = useState(null);
    const [emotionResult, setEmotionResult] = useState('');
    const [message, setMessage] = useState('');
    const [chatResponse, setChatResponse] = useState('');
    const [error, setError] = useState('');

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onloadend = () => {
                setImage(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handleDetectFace = async () => {
        if (image) {
            console.log('Sending image:', image);
            try {
                const response = await axios.post('http://127.0.0.1:5000/detect_face', { image });
                setEmotionResult(response.data.result);
                setError('');
            } catch (error) {
                console.error('Error detecting emotion:', error);
                setError('表情检测请求失败，请检查控制台日志');
            }
        }
    };

    const handleChat = async () => {
        if (message) {
            try {
                const response = await axios.post('http://127.0.0.1:5000/chat', { message });
                setChatResponse(response.data.response);
                setError('');
            } catch (error) {
                console.error('Error chatting with LLM:', error);
                setError('与大语言模型交互请求失败，请检查控制台日志');
            }
        }
    };

    return (
        <div className="App">
            <h1>人脸表情识别与大语言模型交互</h1>
            <input type="file" onChange={handleImageChange} />
            <button onClick={handleDetectFace}>检测表情</button>
            {emotionResult && <p>{emotionResult}</p>}
            <input
                type="text"
                placeholder="输入消息与大语言模型交互"
                value={message}
                onChange={(e) => setMessage(e.target.value)}
            />
            <button onClick={handleChat}>发送消息</button>
            {chatResponse && <p>大语言模型回复: {chatResponse}</p>}
            {error && <p style={{ color: 'red' }}>{error}</p>}
        </div>
    );
}

export default App;
